mapper.select:
SELECT T .*, s.DISPLAY_NAME FROM ACCOUNT_MANAGER_PARAMETER T LEFT JOIN SYS_USER s ON T .USER_ID = s. ID WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
ORDER BY t.CREATED_TIME DESC

SELECT COUNT(*) FROM (SELECT T .*, s.DISPLAY_NAME FROM ACCOUNT_MANAGER_PARAMETER T INNER JOIN SYS_USER s ON T .USER_ID = s. ID WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
ORDER BY t.CREATED_TIME DESC) as a

select * from( select b.display_name, a.base_pay, a.MANAGER_TYPE, a.id, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from account_manager_parameter a, sys_user b where a.user_id = b.id) as ab where 1=1
AND DISPLAY_NAME=#{displayName}
AND instcode=#{organName}
order by instcode

SELECT COUNT(*) FROM ( select * from ( select b.display_name, a.base_pay, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from account_manager_parameter a, sys_user b where a.user_id = b.id) as aaaa where 1=1
AND DISPLAY_NAME=#{displayName}
AND instcode=#{organName}
order by instcode ) as ab

select * from( select a.id, a.MONTH_LOAN_NUM, a.MONTH_EFFECT_NUM, a.MONTH_OVERDUE_LOANNUM, a.MONTH_OVERDUE_DAYS, a.YEAR, a.MONTH, b.display_name as CUSTOMER_MANAGER_ID, a.MONTH_TIMES, b.id as USER_ID, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from T_JX_PARAMETERS a, SYS_USER b where a.customer_manager_id = b.id) as aaaa where 1=1
AND CUSTOMER_MANAGER_ID =#{managerName}
AND instcode=#{organName}
AND YEAR=#{year}
AND MONTH=#{month}
AND USER_ID=#{customerId}
order by instcode

SELECT COUNT(*) FROM ( select * from( select a.id, a.MONTH_LOAN_NUM, a.MONTH_EFFECT_NUM, a.MONTH_OVERDUE_LOANNUM, a.MONTH_OVERDUE_DAYS, a.YEAR, a.MONTH, b.display_name as CUSTOMER_MANAGER_ID, a.MONTH_TIMES, b.id as USER_ID, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from T_JX_PARAMETERS a, SYS_USER b where a.customer_manager_id = b.id) as aaaa where 1=1
AND CUSTOMER_MANAGER_ID =#{managerName}
AND instcode=#{organName}
AND YEAR=#{year}
AND MONTH=#{month}
AND USER_ID=#{customerId}
order by instcode ) as ab

select * from( select a.id, a.year, a.month, a.month_day_average_cust_loanamt, a.prod_limit, a.prod_type, c.chinese_name as customer_id, b.display_name as customer_manager_id, b.id as manger_id, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from t_jx_specific_parameters a, sys_user b, basic_customer_information c where a.CUSTOMER_MANAGER_ID= b.id and c.id = a.customer_id ) as aaaa where 1=1
AND CUSTOMER_MANAGER_ID =#{managerName}
AND instcode=#{organName}
AND YEAR=#{year}
AND MONTH=#{month}
AND MANGER_ID=#{customerId}
order by instcode

SELECT COUNT(*) FROM ( select * from( select a.id, a.year, a.month, a.month_day_average_cust_loanamt, a.prod_limit, a.prod_type, c.chinese_name as customer_id, b.display_name as customer_manager_id, b.id as manger_id, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from t_jx_specific_parameters a, sys_user b, basic_customer_information c where a.CUSTOMER_MANAGER_ID= b.id and c.id = a.customer_id ) as bbbb where 1=1
AND CUSTOMER_MANAGER_ID =#{managerName}
AND instcode=#{organName}
AND YEAR=#{year}
AND MONTH=#{month}
AND MANGER_ID=#{customerId}
order by instcode ) as ab
